import BookList from "./BookList";
import AddBook from "./AddBook";
import {useState} from "react";

function App() {

  const [books , setBooks] = useState([])

  const handleAddBook = (book) => {
    console.log(book)
    setBooks(book)
  }
  return (
    <div className="App">
    <p>Books </p>
      <BookList books={books}/>
      <AddBook handleAddBook={handleAddBook}/>
    </div>
  );
}

export default App;
