  const handleAddBook = (book) => {
    setBooks([...books, book])
  }
    const handleRemoveBook = (id) => {
    setBooks(books.filter(( book) => { return book.id !== id }))
  }
  return (
    <div className="App">
    <p>Books </p>
      <BookList books={books}/>
      <AddBook handleAddBook={handleAddBook}/>
    </div>
  );
}

export default App;
