import {useState} from "react";

function BookEdit( {book}) {
    const [title, setTitle] = useState(book.title)
    const handleChange = (event) => {
        setTitle(event.target.value )
    }
    const handleSubmit = (event) => {
        event.preventDefault()

    }
    return (
        <div className="book-edit">
           <form onSubmit={handleSubmit} className="book-edit">
              <label>Title</label>
               <input  onChange={handleChange} value= {title } className="input" type="text"/>
               <button className="button is-primary">Save</button>
           </form>
        </div>
    )
}

function BookShow({book, onDelete}){
    const [showEdit, setShowEdit] = useState(false)
    const handleClick = ()  => {
        onDelete(book.id)
    }
    const handleEdit = () => {
        setShowEdit(!showEdit)
    }

    console.log(showEdit)
    return (
        <div className="book-show">
            {!showEdit ? <p>{book.title}</p> : <BookEdit book={book} /> }
            <div className="actions">
                <button onClick={handleClick} className="delete"> Delete</button>
                <button onClick={handleEdit} className="edit">Save</button>
            </div>
        </div>
    )
}

export default BookShow
