    const [images , setImages] = useState([])
    const onSubmit = async (term) => {
    // ok, now we can search using this term
    const result = await searchImages(term)
        setImages(result)

}

  return (
    <div className="App">
      <SearchBar onSubmit={ onSubmit }/>
        <ImageList images={images}/>
    </div>
  );
}

export default App;
